import React, { Component } from 'react';
import Button from '@material-ui/core/Button';

import './Question.css';
import { Link } from "react-router-dom";

class Question extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questionNumber: 0,
      questions: [
        {
          question: "Question 1",
          answer1: "Q1 Answer 1",
          answer2: "Q1 Answer 2"
        },
        {
          question: "Question 2",
          answer1: "Q2 Answer 1",
          answer2: "Q2 Answer 2"
        },
        {
          question: "Question 3",
          answer1: "Q3 Answer 1",
          answer2: "Q3 Answer 2"
        }
      ]
    }
    this.incrementQuestionNumber = this.incrementQuestionNumber.bind(this);
  }

  incrementQuestionNumber() {
    var questionNumber = this.state.questionNumber;
    this.setState({ questionNumber: questionNumber + 1 });
    console.log(this.state)
  }

  render() {
    return (
      <div className="Question">
        <header className="Question-header">
          <p>
            {this.state.questions[this.state.questionNumber].question}
          </p>
          <Button onClick={this.incrementQuestionNumber}>
            Choice1
          </Button>
          <Button onClick={this.incrementQuestionNumber}>
            Choice2
          </Button>
        </header>
      </div>
    );
  }
}

export default Question;
