import React, { Component } from 'react';
import Button from '@material-ui/core/Button';

import './Question.css';
import { Redirect } from "react-router-dom";

class Question extends Component {
  constructor(props) {
    super(props);
    this.state = {
      questionNumber: 0,
      questions: [
        {
          question: "Question 1",
          answer1: "Q1 Answer 1",
          answer2: "Q1 Answer 2"
        },
        {
          question: "Question 2",
          answer1: "Q2 Answer 1",
          answer2: "Q2 Answer 2"
        },
        {
          question: "Question 3",
          answer1: "Q3 Answer 1",
          answer2: "Q3 Answer 2"
        }
      ],
      responses: [],
    }
    this.incrementQuestionNumber = this.incrementQuestionNumber.bind(this);
    this.saveAnswer = this.saveAnswer.bind(this);
  }

  incrementQuestionNumber() {
    let questionNumber = this.state.questionNumber;
    this.setState({ questionNumber: questionNumber + 1 });
    console.log(this.state)
  }

  saveAnswer(response) {
    let responses = this.state.responses;
    responses.push(response);
    this.setState({ responses: responses })
    console.log(this.state);
  }

  render() {
    if (this.state.questionNumber == 3) {
      return (
        <Redirect to="/result" />
        // <div className="Question">
        //   <header className="Question-header">
        //     <Link to='/result'>
        //       <Button>
        //         See Results
        //       </Button>
        //     </Link>
        //   </header>
        // </div>
      )
    }

    let question = this.state.questions[this.state.questionNumber].question;
    let answer1 = this.state.questions[this.state.questionNumber].answer1;
    let answer2 = this.state.questions[this.state.questionNumber].answer2;

    return (
      <div className="Question">
        <header className="Question-header">
          <p>
            {question}
          </p>
          <Button onClick={() => { this.incrementQuestionNumber(); this.saveAnswer(answer1)}}>
            {answer1}
          </Button>
          <Button onClick={() => { this.incrementQuestionNumber(); this.saveAnswer(answer2)}}>
            {answer2}
          </Button>
        </header>
      </div>
    );
  }
}

export default Question;
